[{"fieldname": "print_heading_template", "fieldtype": "Custom HTML", "options": "{# --- تحديد نوع الفاتورة بناءً على buyer_vat_registration_number --- #}
{% set invoice_type = "Simplified" %}
{% if doc.custom_zatca_siaf %}
    {% set buyer_vat = frappe.db.get_value("Sales Invoice Additional Fields", doc.custom_zatca_siaf, "buyer_vat_registration_number") %}
    {% if buyer_vat %}
        {% set invoice_type = "Standard" %}
    {% endif %}
{% endif %}

{# --- تحديد عنوان الفاتورة --- #}
{% if invoice_type == "Standard" %}
    {% if doc.is_return %}
        {% set invoice_title = "إشعار دائن ضريبي قياسي" %}
    {% elif doc.is_debit_note %}
        {% set invoice_title = "إشعار مدين ضريبي قياسي" %}
    {% else %}
        {% set invoice_title = "فاتورة ضريبية قياسية" %}
    {% endif %}
{% else %}
    {% if doc.is_return %}
        {% set invoice_title = "إشعار دائن ضريبي مبسط" %}
    {% elif doc.is_debit_note %}
        {% set invoice_title = "إشعار مدين ضريبي مبسط" %}
    {% else %}
        {% set invoice_title = "فاتورة ضريبية مبسطة" %}
    {% endif %}
{% endif %}

<!-- هيدر الفاتورة متوافق مع PDF -->
<div style="text-align: center; margin-bottom: 10px;">
    <div style="font-size: 2.1em; font-weight: bold; margin-top: 18px; color: #222; font-family: Arial, sans-serif;">{{ invoice_title }}</div>
    {% set branch_name = frappe.db.get_value("Cost Center", doc.cost_center, "cost_center_name") if doc.cost_center else "" %}
    {% if branch_name %}
        <div style="font-size: 15px; color: #444; margin: 4px 0; font-family: Arial, sans-serif;">الفرع: <span style="color: #777;">{{ branch_name }}</span></div>
    {% endif %}
    <table style="width: 100%; margin-top: 16px; border-collapse: collapse; font-family: Arial, sans-serif; direction: rtl;">
        <tr>
            <td style="width: 50%; text-align: center; font-size: 16px; font-weight: bold; border: 1px solid #bbb; padding: 8px;">
                تاريخ الفاتورة: {{ doc.get_formatted('posting_date') }}
            </td>
            <td style="width: 50%; text-align: center; font-size: 16px; font-weight: bold; border: 1px solid #bbb; padding: 8px;">
                رقم الفاتورة: {{ doc.name }}
            </td>
        </tr>
    </table>
</div>

"}, {"fieldtype": "Section Break", "label": ""}, {"fieldtype": "Column Break"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "<div>
  <table style="width: 100%; border-collapse: collapse; direction: rtl; font-family: Arial, sans-serif; font-size: 14px; border: 1px solid #bbb; border-radius: 6px;">
    <thead>
      <tr>
        <th style="background-color: #f5f5f5; font-weight: bold; font-size: 15px; padding: 6px 8px; border: 1px solid #bbb; border-top-right-radius: 6px; text-align: center; vertical-align: middle;" colspan="2">بيانات البائع</th>
        <th style="background-color: #f5f5f5; font-weight: bold; font-size: 15px; padding: 6px 8px; border: 1px solid #bbb; border-top-left-radius: 6px; text-align: center; vertical-align: middle;" colspan="2">بيانات المشتري</th>
      </tr>
    </thead>
    <tbody>
      {% set company_name = doc.company %}
      {% set company_data = frappe.get_doc("Company", company_name) %}
      {% set company_ar_name = company_data.custom_company_arabic_name if company_data else "" %}
      {% set company_cr_no = company_data.custom_commercial_registration_number if company_data else "" %}
      {% set address_name_seller = frappe.db.get_value("Dynamic Link", {"link_doctype": "Company", "link_name": company_name}, "parent") %}
      {% set address_line1_s, city_s, pincode_s = frappe.db.get_value("Address", address_name_seller, ["address_line1", "city", "pincode"]) if address_name_seller else ("", "", "") %}

      {% set address_name_buyer = frappe.db.get_value("Dynamic Link", {"link_doctype": "Customer", "link_name": doc.customer}, "parent") %}
      {% set address_line1_b, city_b, pincode_b = frappe.db.get_value("Address", address_name_buyer, ["address_line1", "city", "pincode"]) if address_name_buyer else ("", "", "") %}
      {% set vat_no_buyer = frappe.db.get_value('Customer', doc.customer, 'custom_vat_registration_number') %}
      {% set cr_no_buyer = frappe.db.get_value("Additional Buyer IDs", {"parent": doc.customer, "type_name": "Commercial Registration Number"}, "value") %}

      <tr>
        <td style="width: 15%; background-color: #fff; font-weight: bold; font-size: 14px; border: 1px solid #bbb; padding: 5px 7px; text-align: center; vertical-align: middle;">اسم الشركة</td>
        <td style="width: 35%; background-color: #fff; border: 1px solid #bbb; padding: 5px 7px; text-align: center; vertical-align: middle;">{{ company_ar_name or "" }}</td>
        <td style="width: 15%; background-color: #fff; font-weight: bold; font-size: 14px; border: 1px solid #bbb; padding: 5px 7px; text-align: center; vertical-align: middle;">اسم الشركة</td>
        <td style="width: 35%; background-color: #fff; border: 1px solid #bbb; padding: 5px 7px; text-align: center; vertical-align: middle;">{{ doc.customer_name }}</td>
      </tr>

      <tr>
        <td style="width: 15%; background-color: #fff; font-weight: bold; font-size: 14px; border: 1px solid #bbb; padding: 5px 7px; text-align: center; vertical-align: middle;">العنوان</td>
        <td style="width: 35%; background-color: #fff; border: 1px solid #bbb; padding: 5px 7px; text-align: center; vertical-align: middle;">
          {{ address_line1_s }}{% if city_s %}, {{ city_s }}{% endif %}{% if pincode_s %}, {{ pincode_s }}{% endif %}
        </td>
        <td style="width: 15%; background-color: #fff; font-weight: bold; font-size: 14px; border: 1px solid #bbb; padding: 5px 7px; text-align: center; vertical-align: middle;">العنوان</td>
        <td style="width: 35%; background-color: #fff; border: 1px solid #bbb; padding: 5px 7px; text-align: center; vertical-align: middle;">
          {{ address_line1_b }}{% if city_b %}, {{ city_b }}{% endif %}{% if pincode_b %}, {{ pincode_b }}{% endif %}
        </td>
      </tr>

      <tr>
        <td style="width: 15%; background-color: #fff; font-weight: bold; font-size: 14px; border: 1px solid #bbb; padding: 5px 7px; text-align: center; vertical-align: middle;">الرقم الضريبي</td>
        <td style="width: 35%; background-color: #fff; border: 1px solid #bbb; padding: 5px 7px; text-align: center; vertical-align: middle;">{{ doc.company_tax_id or "" }}</td>
        <td style="width: 15%; background-color: #fff; font-weight: bold; font-size: 14px; border: 1px solid #bbb; padding: 5px 7px; text-align: center; vertical-align: middle;">الرقم الضريبي</td>
        <td style="width: 35%; background-color: #fff; border: 1px solid #bbb; padding: 5px 7px; text-align: center; vertical-align: middle;">{{ vat_no_buyer or "" }}</td>
      </tr>

      <tr>
        <td style="width: 15%; background-color: #fff; font-weight: bold; font-size: 14px; border: 1px solid #bbb; padding: 5px 7px; text-align: center; vertical-align: middle; border-bottom-right-radius: 6px;">السجل التجاري</td>
        <td style="width: 35%; background-color: #fff; border: 1px solid #bbb; padding: 5px 7px; text-align: center; vertical-align: middle;">{{ company_cr_no or "" }}</td>
        <td style="width: 15%; background-color: #fff; font-weight: bold; font-size: 14px; border: 1px solid #bbb; padding: 5px 7px; text-align: center; vertical-align: middle;">السجل التجاري</td>
        <td style="width: 35%; background-color: #fff; border: 1px solid #bbb; padding: 5px 7px; text-align: center; vertical-align: middle; border-bottom-left-radius: 6px;">{{ cr_no_buyer or "" }}</td>
      </tr>
    </tbody>
  </table>
</div>


"}, {"fieldtype": "Section Break", "label": ""}, {"fieldtype": "Column Break"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "
{% if doc.items %}
<div style="margin: 5px 0; padding: 5px; background-color: #fafafa; border: 1px solid #bbb; border-radius: 6px;">
    <table style="width: 100%; border-collapse: collapse; direction: rtl; font-family: Arial, sans-serif; font-size: 13px;">
        <thead>
            <tr style="background-color: #f5f5f5; color: #222; font-weight: bold;">
                <th style="width: 25px; text-align: center; padding: 5px; vertical-align: middle; border: 1px solid #bbb;">م</th>
                <th style="width: 300px; text-align: center; padding: 5px; vertical-align: middle; border: 1px solid #bbb;">الصنف</th>
                <th style="width: 80px; text-align: center; padding: 5px; vertical-align: middle; border: 1px solid #bbb;">الكمية</th>
                <th style="width: 80px; text-align: center; padding: 5px; vertical-align: middle; border: 1px solid #bbb;">الوحدة</th>
                <th style="width: 80px; text-align: center; padding: 5px; vertical-align: middle; border: 1px solid #bbb;">السعر</th>
                <th style="width: 80px; text-align: center; padding: 5px; vertical-align: middle; border: 1px solid #bbb;">المبلغ</th>
            </tr>
        </thead>
        <tbody>
            {%- for row in doc.items -%}
            <tr style="font-size: 13px; text-align: center; border: 1px solid #bbb;">
                <td style="border: 1px solid #bbb; padding: 4px;">{{ row.idx }}</td>
                <td style="border: 1px solid #bbb; padding: 4px;">{{ row.description }}</td>
                <td style="border: 1px solid #bbb; padding: 4px;">{{ row.qty }}</td>
                <td style="border: 1px solid #bbb; padding: 4px;">{{ row.uom }}</td>
                <td style="border: 1px solid #bbb; padding: 4px;">{{ "%.2f"|format(row.rate|float) }}</td>
                <td style="border: 1px solid #bbb; padding: 4px;">{{ "%.2f"|format(row.amount|float) }}</td>
            </tr>
            {%- endfor -%}
        </tbody>
    </table>
</div>
{% endif %}
"}, {"fieldtype": "Section Break", "label": ""}, {"fieldtype": "Column Break"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "<div style="display: inline-block; margin: 5px 0; padding: 5px; background-color: #fafafa; border: 1px solid #bbb; border-radius: 6px;">
    <div style="text-align: center; font-size: 14px; font-weight: bold; padding: 2px; margin-bottom: 2px; background-color: #f5f5f5; border-radius: 4px; font-family: Arial, sans-serif;">
        تفاصيل الإجمالي
    </div>

    <table style="border-collapse: collapse; direction: rtl; font-family: Arial, sans-serif; font-size: 13px;">
        <tbody>

            <!-- net_total -->
            {% if doc.net_total %}
            <tr>
                <th style="text-align: right; padding: 4px; border: 1px solid #bbb; background-color: #f5f5f5; font-weight: bold; white-space: nowrap;">الإجمالي الصافي الخاضع للضريبة</th>
                <td style="text-align: center; padding: 4px; border: 1px solid #bbb; font-weight: bold; color: #0A9928; white-space: nowrap;">{{ "{:.2f}".format(doc.net_total | abs) }} ريال</td>
            </tr>
            {% endif %}

            <!-- total_taxes_and_charges -->
            {% if doc.total_taxes_and_charges %}
            <tr>
                <th style="text-align: right; padding: 4px; border: 1px solid #bbb; background-color: #f5f5f5; font-weight: bold; white-space: nowrap;">إجمالي ضريبة القيمة المضافة 15%</th>
                <td style="text-align: center; padding: 4px; border: 1px solid #bbb; font-weight: bold; color: #0A9928; white-space: nowrap;">{{ "{:.2f}".format(doc.total_taxes_and_charges | abs) }} ريال</td>
            </tr>
            {% endif %}

            <!-- discount_amount -->
            {% if doc.discount_amount %}
            <tr style="font-size: 12px; text-align: center;">
                <th colspan="2" style="font-weight: 600; border: 1px solid #bbb; white-space: nowrap;">الخصم</th>
                <td style="font-weight: 600;border: 1px solid #bbb;text-align: center; white-space: nowrap;">{{ "{:.2f}".format(doc.discount_amount) }} ريال</td>
            </tr>
            {% endif %}

            <!-- grand_total -->
            {% if doc.grand_total %}
            <tr>
                <th style="text-align: right; padding: 5px; border: 1px solid #bbb; background-color: #f5f5f5; font-weight: bold; font-size: 1.1em; white-space: nowrap;">الإجمالي مع الضريبة (المستحق)</th>
                <td style="text-align: center; padding: 5px; border: 1px solid #bbb; font-weight: bold; color: #0A9928; white-space: nowrap;">{{ "{:.2f}".format(doc.grand_total | abs) }} ريال</td>
            </tr>
            {% endif %}

        </tbody>
    </table>
</div>
"}, {"fieldtype": "Column Break"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "<div style="margin: 2px 0; width: 180px; height: 180px; float: right;">
    {% if doc.custom_qr_image_src %}
        <img src="{{ doc.custom_qr_image_src }}" style="max-width: 100%; max-height: 100%;">
    {% else %}
        <p style="color: red; font-weight: bold; text-align: center; padding: 5px; margin: 0;">
            الرمز لم يتم إنشاءه
        </p>
    {% endif %}
</div>
"}, {"fieldtype": "Section Break", "label": ""}, {"fieldtype": "Column Break"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "{% if doc.status %}

    <!-- 🏷️ قسم ترجمة الحالات -->
    {% set status_translation = {
        "Draft": "مسودة",
        "Return": "مرتجع",
        "Credit Note Issued": "تم إصدار إشعار دائن",
        "Submitted": "تم الإرسال",
        "Paid": "مدفوع",
        "Partly Paid": "مدفوع جزئياً",
        "Unpaid": "غير مدفوع",
        "Unpaid and Discounted": "غير مدفوع مع خصم",
        "Partly Paid and Discounted": "مدفوع جزئياً مع خصم",
        "Overdue and Discounted": "متأخر مع خصم",
        "Overdue": "متأخر",
        "Cancelled": "ملغي",
        "Internal Transfer": "تحويل داخلي"
    } %}

    <!-- 🖍️ تخصيص التصميم بناءً على الحالة -->

    {% set translated_status = status_translation.get(doc.status, doc.status) %}

    <!-- 🏷️ عرض الختم بمحاذاة منتصف أسفل قسم الإجماليات مع دمج style مباشرة -->
    <div style="width: 100%; text-align: center; margin: 10px 0 0 0;">
        {% if doc.status == "Paid" %}
        <span style="font-size: 15px; font-weight: bold; padding: 5px 22px; border-radius: 7px; font-family: Arial, sans-serif; display: inline-block; line-height: 1.2; text-align: center; background:#e6f7ea; color:#0A9928; border:1.2px solid #0A9928;">
            {{ translated_status }}
        </span>
        {% elif doc.status == "Partly Paid" %}
        <span style="font-size: 15px; font-weight: bold; padding: 5px 22px; border-radius: 7px; font-family: Arial, sans-serif; display: inline-block; line-height: 1.2; text-align: center; background:#fffbe6; color:#FFC107; border:1.2px solid #FFC107;">
            {{ translated_status }}
        </span>
        {% elif doc.status == "Unpaid" %}
        <span style="font-size: 15px; font-weight: bold; padding: 5px 22px; border-radius: 7px; font-family: Arial, sans-serif; display: inline-block; line-height: 1.2; text-align: center; background:#fdeaea; color:#D23; border:1.2px solid #D23;">
            {{ translated_status }}
        </span>
        {% elif doc.status == "Overdue" %}
        <span style="font-size: 15px; font-weight: bold; padding: 5px 22px; border-radius: 7px; font-family: Arial, sans-serif; display: inline-block; line-height: 1.2; text-align: center; background:#fdeaea; color:#D23; border:1.2px solid #D23;">
            {{ translated_status }}
        </span>
        {% elif doc.status == "Cancelled" %}
        <span style="font-size: 15px; font-weight: bold; padding: 5px 22px; border-radius: 7px; font-family: Arial, sans-serif; display: inline-block; line-height: 1.2; text-align: center; background:#f5f5f5; color:#6C757D; border:1.2px solid #6C757D;">
            {{ translated_status }}
        </span>
        {% elif doc.status == "Submitted" %}
        <span style="font-size: 15px; font-weight: bold; padding: 5px 22px; border-radius: 7px; font-family: Arial, sans-serif; display: inline-block; line-height: 1.2; text-align: center; background:#eaf1fb; color:#007BFF; border:1.2px solid #007BFF;">
            {{ translated_status }}
        </span>
        {% elif doc.status == "Draft" %}
        <span style="font-size: 15px; font-weight: bold; padding: 5px 22px; border-radius: 7px; font-family: Arial, sans-serif; display: inline-block; line-height: 1.2; text-align: center; background:#f5f5f5; color:#C4C4C4; border:1.2px solid #C4C4C4;">
            {{ translated_status }}
        </span>
        {% else %}
        <span style="font-size: 15px; font-weight: bold; padding: 5px 22px; border-radius: 7px; font-family: Arial, sans-serif; display: inline-block; line-height: 1.2; text-align: center; background:#f5f5f5; color:#222; border:1.2px solid #bbb;">
            {{ translated_status }}
        </span>
        {% endif %}
    </div>

{% endif %}
"}, {"fieldtype": "Column Break"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "{% if doc.owner %}

    <!-- 🏷️ استخراج اسم المستخدم -->
    {% set created_by = frappe.db.get_value("User", doc.owner, "full_name") or doc.owner %}

    <!-- 🏷️ عرض ختم صغير للمستخدم -->
    <div style="width: 100%; text-align: right; margin-top: 8px; padding-right: 32px;">
        <span style="font-size: 12px; font-weight: bold; border: 1.2px solid #1976d2; padding: 3px 10px; border-radius: 7px; font-family: 'Arial', sans-serif; background: #e3f0fb; color: #1976d2; display: inline-block; line-height: 1.3; text-align: center; letter-spacing: 0.5px;">
            تم الإنشاء<br>{{ created_by }}
        </span>
    </div>

{% endif %}
"}]
